CREATE TABLE `user` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `name` varchar(45) NOT NULL,  `password` char(32) NOT NULL,  `nickname` varchar(45) NOT NULL,  `ip` varchar(15) DEFAULT NULL,  `login_time` int(10) unsigned DEFAULT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `name_UNIQUE` (`name`)) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;CREATE TABLE `category` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `name` varchar(45) NOT NULL,  `create_time` int(10) unsigned NOT NULL,  `user_id` int(10) unsigned NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `name_UNIQUE` (`name`),  KEY `fk_category_user1_idx` (`user_id`),  CONSTRAINT `fk_category_user1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;CREATE TABLE `article` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `title` varchar(45) NOT NULL,  `content` text,  `create_time` int(10) unsigned NOT NULL,  `category_id` int(10) unsigned NOT NULL,  `user_id` int(10) unsigned NOT NULL,  PRIMARY KEY (`id`),  KEY `fk_article_category_idx` (`category_id`),  KEY `fk_article_user1_idx` (`user_id`),  CONSTRAINT `fk_article_category` FOREIGN KEY (`category_id`) REFERENCES `category` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,  CONSTRAINT `fk_article_user1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;